const item = $input.item;
let senderId, platform, messageText, imageUrlOrFileId, originalPayload, messageType = 'unknown', apartmentNumber = null, rawMessage = {};

if (item.json.body && item.json.body.data && item.json.body.data.key && item.json.body.data.key.remoteJid) { // Evolution API (WhatsApp)
    platform = 'whatsapp';
    senderId = item.json.body.data.key.remoteJid;
    originalPayload = item.json.body.data;
    rawMessage = item.json.body.data.message || {};

    if (rawMessage.imageMessage) {
        imageUrlOrFileId = rawMessage.imageMessage.url || `https://YOUR_EVOLUTION_API_INSTANCE/chat/getBase64File?msgId=${item.json.body.data.key.id}`; 
        messageText = rawMessage.imageMessage.caption || '';
    } else if (rawMessage.extendedTextMessage) {
        messageText = rawMessage.extendedTextMessage.text || '';
    } else if (rawMessage.conversation) {
        messageText = rawMessage.conversation || '';
    } else {
        messageText = '';
    }

} else if (item.json.message) { // Telegram
    platform = 'telegram';
    senderId = item.json.message.from.id.toString();
    originalPayload = item.json.message;
    rawMessage = item.json.message;
    messageText = rawMessage.text || rawMessage.caption || '';

    let fileId = null;
    if (rawMessage.photo && rawMessage.photo.length > 0) {
        fileId = rawMessage.photo[rawMessage.photo.length - 1].file_id;
    } else if (rawMessage.document && rawMessage.document.mime_type && rawMessage.document.mime_type.startsWith('image')) {
        fileId = rawMessage.document.file_id;
    }
    imageUrlOrFileId = fileId;
} else {
    return [{
        json: {
            error: "Webhook no reconocido o formato inesperado.",
            platform: 'unknown',
            senderId: 'unknown',
            originalPayload: { error_source: 'Webhook', input: JSON.stringify(item.json).substring(0, 200) },
            messageType: 'system_error',
            responseText: "Error: No se pudo procesar el mensaje entrante (formato desconocido). Por favor, contacte al administrador.",
            responseType: "text_message"
        }
    }];
}

const aptRegex = /(?:apto|apartamento|ap|casa|local|oficina|vivienda)\s*([a-zA-Z0-9\s\-]+(?:\(?[PH|Piso|Torre|Etapa|Manzana|Mz\.?\s*\d+\)?)?)/i;
const aptMatch = messageText.match(aptRegex);
if (aptMatch && aptMatch[1]) {
    apartmentNumber = aptMatch[1].trim().toUpperCase();
}

if (imageUrlOrFileId && apartmentNumber) {
    messageType = 'payment_ingest';
} else if (messageText.trim().length > 0) {
    messageType = 'data_query';
} else {
    messageType = 'unknown_type'; 
}

return [{
    json: {
        senderId: senderId,
        platform: platform,
        messageText: messageText.trim(),
        imageUrlOrFileId: imageUrlOrFileId, 
        apartmentNumber: apartmentNumber,
        originalPayload: originalPayload, 
        messageType: messageType,
        timestamp: new Date().toISOString()
    }
}];

const llmNodeOutput = $('4A. LLM - Extraer Datos de Recibo').item ? $('4A. LLM - Extraer Datos de Recibo').item.json : null;
const llmOutput = llmNodeOutput ? llmNodeOutput.response : null; 
const { apartmentNumber, senderId, platform, originalPayload } = $input.item.json;

let montoPagado = 0;
let fechaPago = 'N/A';
let tipoTransaccion = 'N/A';
let numeroReferencia = 'N/A';

if (!llmOutput) { 
    return [{ json: { 
        responseText: 'Error: No se pudo obtener una respuesta del an√°lisis de la imagen.', 
        senderId: senderId, platform: platform, originalPayload: originalPayload,
        responseType: 'text_message'
    }}];
}

if (llmOutput) {
    if (typeof llmOutput.monto_pagado === 'string') {
        const cleanedAmount = llmOutput.monto_pagado.replace(/[^0-9.,-]+/g, '').replace(',', '.');
        montoPagado = parseFloat(cleanedAmount) || 0;
    } else if (typeof llmOutput.monto_pagado === 'number'){
        montoPagado = llmOutput.monto_pagado;
    }
    fechaPago = llmOutput.fecha_pago || 'N/A';
    tipoTransaccion = llmOutput.tipo_transaccion || 'N/A';
    numeroReferencia = llmOutput.numero_referencia || 'N/A';
}

if (montoPagado <= 0 && (tipoTransaccion === 'N/A' || tipoTransaccion === 'Otro')) { 
    return [{ json: { 
        responseText: '‚ö†Ô∏è No se pudieron extraer datos v√°lidos del recibo (monto/tipo). Por favor, aseg√∫rate que la imagen sea clara y el texto legible.', 
        llm_raw_extraction: JSON.stringify(llmOutput),
        senderId: senderId, platform: platform, originalPayload: originalPayload,
        responseType: 'text_message'
    }}];
}

if (fechaPago !== 'N/A' && !/^\d{4}-\d{2}-\d{2}$/.test(fechaPago)) {
    console.warn(`Fecha extra√≠da '${fechaPago}' no est√° en formato YYYY-MM-DD. Usando fecha actual.`);
    fechaPago = new Date().toISOString().split('T')[0]; 
}
if (fechaPago === 'N/A') {
    console.warn(`Fecha extra√≠da es 'N/A'. Usando fecha actual.`);
    fechaPago = new Date().toISOString().split('T')[0];
}

const paymentData = {
    apartment_number: apartmentNumber,
    paid_amount: montoPagado,
    payment_date: fechaPago, 
    transaction_type: tipoTransaccion,
    reference_number: numeroReferencia,
    registration_date: new Date().toISOString(),
    registered_by_platform: platform,
    registered_by_sender: senderId,
    llm_raw_extraction: JSON.stringify(llmOutput)
};

return [{
    json: {
        ...paymentData,
        senderId: senderId, platform: platform, originalPayload: originalPayload,
        apartment_number_display: apartmentNumber, 
        paid_amount_display: montoPagado.toFixed(2),
        payment_date_display: fechaPago,
        _intermediateType: 'ingest_parsed_success' 
    }
}];

const { apartment_number_display, paid_amount_display, payment_date_display, transaction_type, reference_number } = $input.item.json;
let responseText = `¬°Gracias! ‚úÖ Tu pago de $${paid_amount_display} para el apto/casa '${apartment_number_display}' con fecha ${payment_date_display} (Tipo: ${transaction_type || 'N/E'}) ha sido registrado. Ref: ${reference_number && reference_number !== 'N/A' ? reference_number : 'N/E'}.`;

// Si el nodo de DB tuvo un error (n8n lo manejar√≠a deteniendo el flujo, pero por si acaso)
const dbNode = $('6A. Guardar Pago en DB');
if (dbNode.error) {
    responseText = '‚ö†Ô∏è Hubo un problema al guardar tu pago en la base de datos. Por favor, contacta al administrador.';
}

return [{ json: { responseText, responseType: 'text_message' } }];

const llmNodeOutput = $('3B. LLM - Interpretar Solicitud de Datos').item ? $('3B. LLM - Interpretar Solicitud de Datos').item.json : null;
const llmResponse = llmNodeOutput ? llmNodeOutput.response : null; 
let intencion = 'desconocido';
let parametros = {};

if (llmResponse && llmResponse.intencion) {
    intencion = llmResponse.intencion;
    parametros = llmResponse.parametros || {};
} else { 
    console.error("Respuesta inesperada del LLM interpretador de consultas:", llmResponse);
    intencion = 'desconocido'; 
}

if (parametros.periodo) {
    parametros.periodo = parametros.periodo.toLowerCase();
    if (['este mes', 'mes corriente'].includes(parametros.periodo)) parametros.periodo = 'mes_actual';
    if (['mes pasado', 'mes anterior'].includes(parametros.periodo)) parametros.periodo = 'mes_anterior';
}

if (parametros.apartamento) {
  parametros.apartamento = parametros.apartamento.toUpperCase();
  if (['TODO', 'TODOS', 'GENERAL', 'GLOBAL'].includes(parametros.apartamento)) {
    parametros.apartamento = 'TODOS';
  }
}
return [{json: {
    intencion,
    parametros,
    senderId: $input.item.json.senderId,
    platform: $input.item.json.platform,
    originalPayload: $input.item.json.originalPayload,
    messageText: $input.item.json.messageText
}}];

const params = $input.item.json.parametros;
let condiciones = []; let queryParams = [];
let dateColumn = 'payment_date'; const now = new Date();
let startDate, endDate;
let periodoText = params.periodo || 'mes_actual';
switch (periodoText) {
    case 'mes_actual': startDate = new Date(now.getFullYear(), now.getMonth(), 1,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0, 23, 59, 59, 999); break;
    case 'mes_anterior': startDate = new Date(now.getFullYear(), now.getMonth() - 1, 1,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth(), 0, 23, 59, 59, 999); break;
    case 'ultimos_7_dias': startDate = new Date(now.getFullYear(), now.getMonth(), now.getDate() - 6,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth(), now.getDate(), 23, 59, 59, 999); break;
    case 'ultimos_30_dias': startDate = new Date(now.getFullYear(), now.getMonth(), now.getDate() - 29,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth(), now.getDate(), 23, 59, 59, 999); break;
    case 'ultimos_3_meses': startDate = new Date(now.getFullYear(), now.getMonth() - 2, 1,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0, 23, 59, 59, 999); break;
    case 'a√±o_actual': startDate = new Date(now.getFullYear(), 0, 1,0,0,0,0); endDate = new Date(now.getFullYear(), 11, 31, 23, 59, 59, 999); break;
    case 'todo': break;
    default: const mY = periodoText.match(/(\d{1,2})\/?(\d{4})/); const yO = periodoText.match(/^(\d{4})$/); if(mY){const m=parseInt(mY[1])-1;const y=parseInt(mY[2]);if(!isNaN(m)&&!isNaN(y)){startDate=new Date(y,m,1,0,0,0,0);endDate=new Date(y,m+1,0,23,59,59,999);}}else if(yO){const y=parseInt(yO[1]);if(!isNaN(y)){startDate=new Date(y,0,1,0,0,0,0);endDate=new Date(y,11,31,23,59,59,999);}}break;
}
if (startDate && endDate) { condiciones.push(`${dateColumn} >= $${queryParams.length + 1} AND ${dateColumn} <= $${queryParams.length + 2}`); queryParams.push(startDate.toISOString().split('T')[0]); queryParams.push(endDate.toISOString().split('T')[0]); }
if (params.apartamento && params.apartamento !== 'TODOS') { condiciones.push(`apartment_number ILIKE $${queryParams.length + 1}`); queryParams.push(`%${params.apartamento}%`); }
if (params.tipo_transaccion) { condiciones.push(`transaction_type ILIKE $${queryParams.length + 1}`); queryParams.push(`%${params.tipo_transaccion}%`); }
let whereClause = condiciones.length > 0 ? `WHERE ${condiciones.join(' AND ')}` : '';
let sqlQuery = `SELECT apartment_number, paid_amount, TO_CHAR(payment_date, 'YYYY-MM-DD') as payment_date_fmt, transaction_type, reference_number FROM condo_payments ${whereClause} ORDER BY payment_date DESC, registration_date DESC, apartment_number ASC LIMIT 50;`;
return [{ json: { sqlQuery, queryParams, periodoSolicitado: periodoText, aptoSolicitado: params.apartamento || 'Todos'} }];

const payments = $input.item.json.data;
const { periodoSolicitado, aptoSolicitado } = $input.item.json;
let responseText = ``;
if (payments && payments.length > 0) {
    responseText = `Pagos (Apto: ${aptoSolicitado}, Periodo: ${periodoSolicitado}):\n`;
    responseText += '`---------------------------------------------------`\n';
    responseText += '`| Apto.  | Monto    | Fecha      | Tipo             | Ref.   |`\n';
    responseText += '`|--------|----------|------------|------------------|--------|`\n';
    payments.forEach(p => {
        const apt = (p.apartment_number || 'N/A').substring(0,6).padEnd(6);
        const monto = (`$` + (parseFloat(p.paid_amount) || 0).toFixed(2)).padEnd(8);
        const fecha = (p.payment_date_fmt || 'N/A').padEnd(10);
        const tipo = (p.transaction_type || 'N/A').substring(0,16).padEnd(16);
        const ref = (p.reference_number || 'N/A').substring(0,6).padEnd(6);
        responseText += '`| ' + apt + ' | ' + monto + ' | ' + fecha + ' | ' + tipo + ' | ' + ref + ' |`\n';
    });
    responseText += '`---------------------------------------------------`\n';
    if (payments.length >= 50) responseText += "\n_(Mostrando hasta 50 regs. Refina tu b√∫squeda.)_";
} else { responseText = `No se encontraron pagos para Apto: ${aptoSolicitado}, Periodo: ${periodoSolicitado}.`; }
return [{ json: { responseText, responseType: 'text_message' } }];

const params = $input.item.json.parametros;
let condiciones = []; let queryParams = []; let dateColumn = 'payment_date'; const now = new Date();
let startDate, endDate;
let periodoText = params.periodo || 'todo';
switch (periodoText) {
    case 'mes_actual': startDate = new Date(now.getFullYear(), now.getMonth(), 1,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0, 23,59,59,999); break;
    case 'mes_anterior': startDate = new Date(now.getFullYear(), now.getMonth() - 1, 1,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth(), 0, 23,59,59,999); break;
    case 'a√±o_actual': startDate = new Date(now.getFullYear(), 0, 1,0,0,0,0); endDate = new Date(now.getFullYear(), 11, 31, 23,59,59,999); break;
    case 'todo': break; 
    default: const mY=periodoText.match(/(\d{1,2})\/?(\d{4})/);if(mY){const m=parseInt(mY[1])-1;const y=parseInt(mY[2]);if(!isNaN(m)&&!isNaN(y)){startDate=new Date(y,m,1,0,0,0,0);endDate=new Date(y,m+1,0,23,59,59,999);}}break;
}
if (startDate && endDate) { condiciones.push(`${dateColumn} >= $${queryParams.length + 1} AND ${dateColumn} <= $${queryParams.length + 2}`); queryParams.push(startDate.toISOString().split('T')[0]); queryParams.push(endDate.toISOString().split('T')[0]); }
let whereClause = condiciones.length > 0 ? `WHERE ${condiciones.join(' AND ')}` : '';
const sqlQuery = `SELECT SUM(paid_amount) as total_paid, COUNT(*) as number_of_payments FROM condo_payments ${whereClause};`;
return [{ json: { sqlQuery, queryParams, periodoSolicitado: periodoText } }];

const result = $input.item.json.data[0];
const totalPaid = result && result.total_paid !== null ? parseFloat(result.total_paid) : 0;
const numPayments = result && result.number_of_payments !== null ? parseInt(result.number_of_payments) : 0;
const periodo = $input.item.json.periodoSolicitado;
const responseText = `Balance General (Periodo: ${periodo}):\n- Total Ingresado: $${totalPaid.toFixed(2)}\n- Nro. Pagos: ${numPayments}`;
return [{ json: { responseText, responseType: 'text_message' } }];

const params = $input.item.json.parametros;
let sqlQuery = ''; let queryParams = []; const now = new Date();
let startDate, endDate; let dateColumn = 'payment_date';
let periodoText = params.periodo || 'mes_actual';
switch (periodoText) {
    case 'mes_actual': startDate = new Date(now.getFullYear(), now.getMonth(), 1,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0,23,59,59,999); break;
    case 'mes_anterior': startDate = new Date(now.getFullYear(), now.getMonth() - 1, 1,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth(), 0,23,59,59,999); break;
    case 'ultimos_7_dias': startDate = new Date(now.getFullYear(), now.getMonth(), now.getDate() - 6,0,0,0,0); endDate = new Date(now.getFullYear(),now.getMonth(),now.getDate(),23,59,59,999); break;
    case 'ultimos_30_dias': startDate = new Date(now.getFullYear(), now.getMonth(), now.getDate() - 29,0,0,0,0); endDate = new Date(now.getFullYear(),now.getMonth(),now.getDate(),23,59,59,999); break;
    case 'ultimos_3_meses': startDate = new Date(now.getFullYear(), now.getMonth() -2, 1,0,0,0,0); endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0,23,59,59,999); break;
    case 'a√±o_actual': startDate = new Date(now.getFullYear(), 0, 1,0,0,0,0); endDate = new Date(now.getFullYear(), 11, 31,23,59,59,999); break;
    case 'todo': break;
    default: const mY=periodoText.match(/(\d{1,2})\/?(\d{4})/);if(mY){const m=parseInt(mY[1])-1;const y=parseInt(mY[2]);if(!isNaN(m)&&!isNaN(y)){startDate=new Date(y,m,1,0,0,0,0);endDate=new Date(y,m+1,0,23,59,59,999);}}break;
}
let dateFilter = '';
if (startDate && endDate) { dateFilter = (queryParams.length > 0 || aptFilter ? ' AND ' : 'WHERE ') + `${dateColumn} >= $${queryParams.length + 1} AND ${dateColumn} <= $${queryParams.length + 2}`; queryParams.push(startDate.toISOString().split('T')[0]); queryParams.push(endDate.toISOString().split('T')[0]); }
let aptFilter = '';
if (params.apartamento && params.apartamento !== 'TODOS') { aptFilter = (queryParams.length > 0 || dateFilter ? ' AND ' : 'WHERE ') + `apartment_number ILIKE $${queryParams.length + 1}`; queryParams.push(`%${params.apartamento}%`);}
let finalWhereClause = (dateFilter + aptFilter).trim();
if (finalWhereClause.startsWith('AND')) { finalWhereClause = 'WHERE ' + finalWhereClause.substring(3).trim(); }

let groupBy = '', selectClause = '', orderBy = '';
switch (params.datos_a_graficar) {
    case 'ingresos_por_mes': selectClause = `TO_CHAR(${dateColumn}, 'YYYY-MM') as label, SUM(paid_amount) as value`; groupBy = `GROUP BY TO_CHAR(${dateColumn}, 'YYYY-MM')`; orderBy = `ORDER BY label ASC`; if (periodoText === 'todo' && !finalWhereClause.includes(`${dateColumn} >=`)) { const tMA = new Date(now.getFullYear()-1,now.getMonth(),1,0,0,0,0); finalWhereClause = (finalWhereClause?finalWhereClause+' AND ':'WHERE ') + `${dateColumn} >= $${queryParams.length+1}`; queryParams.push(tMA.toISOString().split('T')[0]);} break;
    case 'ingresos_por_dia': selectClause = `TO_CHAR(${dateColumn}, 'YYYY-MM-DD') as label, SUM(paid_amount) as value`; groupBy = `GROUP BY TO_CHAR(${dateColumn}, 'YYYY-MM-DD')`; orderBy = `ORDER BY label ASC`; if (periodoText === 'todo' && !finalWhereClause.includes(`${dateColumn} >=`)) { const tDA = new Date(now.getFullYear(),now.getMonth(),now.getDate()-29,0,0,0,0); finalWhereClause = (finalWhereClause?finalWhereClause+' AND ':'WHERE ') + `${dateColumn} >= $${queryParams.length+1}`; queryParams.push(tDA.toISOString().split('T')[0]);} break;
    case 'ingresos_por_apartamento': selectClause = `COALESCE(apartment_number, 'N/Asignado') as label, SUM(paid_amount) as value`; groupBy = `GROUP BY COALESCE(apartment_number, 'N/Asignado')`; orderBy = `ORDER BY value DESC LIMIT 10`; break;
    case 'ingresos_por_tipo_transaccion': selectClause = `COALESCE(transaction_type, 'N/Definido') as label, SUM(paid_amount) as value`; groupBy = `GROUP BY COALESCE(transaction_type, 'N/Definido')`; orderBy = `ORDER BY value DESC`; break;
    case 'conteo_pagos_por_mes': selectClause = `TO_CHAR(${dateColumn}, 'YYYY-MM') as label, COUNT(*) as value`; groupBy = `GROUP BY TO_CHAR(${dateColumn}, 'YYYY-MM')`; orderBy = `ORDER BY label ASC`; if (periodoText === 'todo' && !finalWhereClause.includes(`${dateColumn} >=`)) { const tMA = new Date(now.getFullYear()-1,now.getMonth(),1,0,0,0,0); finalWhereClause = (finalWhereClause?finalWhereClause+' AND ':'WHERE ') + `${dateColumn} >= $${queryParams.length+1}`; queryParams.push(tMA.toISOString().split('T')[0]);} break;
    default: return [{ json: { responseText: `Error: Tipo de datos para graficar '${params.datos_a_graficar}' no soportado.`, responseType: 'text_message' } }];
}
sqlQuery = `SELECT ${selectClause} FROM condo_payments ${finalWhereClause} ${groupBy} ${orderBy};`;
return [{ json: { sqlQuery, queryParams, chartParams: params, periodoSolicitado: periodoText, aptoSolicitado: params.apartamento || 'Todos' } }];

const chartParams = $input.item.json.chartParams;
const dbData = $input.item.json.data;
const { periodoSolicitado, aptoSolicitado } = $input.item.json;

if (!dbData || dbData.length === 0) {
    return [{ json: { responseText: `No hay datos suficientes para generar el gr√°fico '${chartParams.datos_a_graficar}' (Periodo: ${periodoSolicitado}, Apto: ${aptoSolicitado}).`, responseType: 'text_message' } }];
}

const labels = dbData.map(d => d.label || 'N/A');
const dataValues = dbData.map(d => parseFloat(d.value) || 0);
let chartType = chartParams.tipo_grafico; if (chartType === 'torta') chartType = 'pie'; if (chartType === 'lineas') chartType = 'line'; if (chartType === 'barras') chartType = 'bar';
const bgColors = ['#4e79a7','#f28e2c','#e15759','#76b7b2','#59a14f','#edc949','#af7aa1','#ff9da7','#9c755f','#bab0ab'];
const chartConfig = {
    type: chartType, data: { labels: labels, datasets: [{ label: chartParams.datos_a_graficar.replace(/_/g, ' '), data: dataValues, backgroundColor: chartType === 'line' ? 'rgba(75,192,192,0.2)' : bgColors.slice(0, dataValues.length), borderColor: chartType === 'line' ? 'rgba(75,192,192,1)' : bgColors.map(c=>c.replace('0.7','1')).slice(0, dataValues.length), borderWidth: chartType==='pie'?1:2, fill: chartType==='line' }] },
    options: { responsive: true, plugins: { title: { display: true, text: `${chartParams.datos_a_graficar.replace(/_/g, ' ')} (P: ${periodoSolicitado}, A: ${aptoSolicitado})`, font: {size: 16}}, legend: {position: chartType==='pie'?'top':'bottom', display: dataValues.length <= 10 && dataValues.length > 1}}, scales: chartType!=='pie'?{y:{beginAtZero:true, ticks:{callback:v=>'$'+v}}, x:{ticks:{autoSkip:true,maxTicksLimit:labels.length>15?10:20}}}:undefined }
};
const quickChartPayload = { chart: chartConfig, width: 600, height: 400, backgroundColor: 'white', format: 'png' };
const caption = `Gr√°fico: ${chartParams.datos_a_graficar.replace(/_/g, ' ')} (P: ${periodoSolicitado}, A: ${aptoSolicitado})`;
return [{ json: { quickChartPayload, responseType: 'chart_image', imageCaption: caption } }];

const userInput = $input.item.json.messageText || ($input.item.json.originalPayload ? JSON.stringify($input.item.json.originalPayload).substring(0,70) : 'No se pudo obtener el mensaje original.');
const msg = `Lo siento, no entend√≠ tu solicitud: "${userInput.substring(0,50)}${userInput.length > 50 ? '...' : ''}". 

Intenta con 'ayuda' para ver opciones.`;
return [{ json: { responseText: msg, responseType: 'text_message' } }];

const items = $items();
let outputItem = { json: { responseType: 'unknown_final' } };

// Prioritize the first valid item that has a responseType and necessary data
for (const item of items) {
    const currentJson = item.json || {};
    const currentBinary = item.binary || {};

    if (currentJson.responseType === 'text_message' && currentJson.responseText) {
        outputItem.json = {
            responseText: currentJson.responseText,
            responseType: 'text_message',
            senderId: currentJson.senderId || ($input.item.json.senderId), // Fallback to initial senderId
            platform: currentJson.platform || ($input.item.json.platform),
            originalPayload: currentJson.originalPayload || ($input.item.json.originalPayload)
        };
        break;
    } else if (currentJson.responseType === 'chart_image' && currentBinary.data) { // From QuickChart
        outputItem.json = {
            responseType: 'image_message',
            imageCaption: currentJson.imageCaption || 'Gr√°fico Solicitado',
            senderId: currentJson.senderId || ($input.item.json.senderId),
            platform: currentJson.platform || ($input.item.json.platform),
            originalPayload: currentJson.originalPayload || ($input.item.json.originalPayload)
        };
        // CRITICAL: Prepare binary data for sending. Evolution API might need base64.
        // N8N's binary data from HTTP Request (file format) is typically a Buffer.
        const imageBuffer = Buffer.isBuffer(currentBinary.data) ? currentBinary.data : Buffer.from(currentBinary.data);
        outputItem.binary = { 
