                base64: imageBuffer.toString('base64'), // For Evolution API if needed
                fileName: currentBinary.fileName || 'grafico.png',
                mimeType: currentBinary.mimeType || 'image/png'
            }
        }; 
        break;
    }
}

// If no specific response was prepared, but we have initial sender info, send a generic error.
if (outputItem.json.responseType === 'unknown_final' && $input.item.json && $input.item.json.senderId) {
    outputItem.json = {
